import {Alert,Form, Button} from "react-bootstrap";
import '../styles/loginStyle.css';
import {Link} from "react-router-dom";
import {useState} from "react";
import {useHistory} from 'react-router-dom';
import axios from "axios";


const Login = () =>{
    const [email,setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [alert, setAlert] = useState("");
    const [isAlert, setIsAlert] = useState(false);
    const [response,setResponse] = useState();
    let history = useHistory();

    const postRequest = () =>{
        axios.post('/api/auth',{email,password})
            .then((data)=>{
                setResponse(data.data.token);
                localStorage.setItem('token',response);
                history.push('/');
            }).catch((err)=>{
            console.error(err)
            setAlert(err.response.data.errors[0].msg);
            setIsAlert(true);
        });
    }

    const submit = (e) =>{
        e.preventDefault();
        setIsAlert(false);
        postRequest();
    }
    return(
           <>
               {isAlert &&<Alert variant='danger'>{alert}</Alert>}
               <div className="Login">
                   <Form>
                       <Form.Group size="lg" controlId="email">
                           <Form.Label>Email</Form.Label>
                           <Form.Control
                               autoFocus
                               type="email"
                           />
                       </Form.Group>
                       <Form.Group size="lg" controlId="password" className='password'>
                           <Form.Label>Password</Form.Label>
                           <Form.Control
                               type="password"
                           />
                       </Form.Group>
                       <Link to='/register' className='text-black'>
                           <p>Don't have an account?</p>
                       </Link>
                       <Button block size="lg" type="submit" className='btn-dark'>
                           Login
                       </Button>
                   </Form>
               </div>
           </>
    )
}

export default Login;
