import {useEffect, useState} from "react";
import axios from "axios";
import {useHistory} from "react-router-dom";
import {Alert, Button} from "react-bootstrap";
import GoogleMapReact from 'google-map-react';

const Dashboard = () =>{
    const [alert, setAlert] = useState('');
    const [isAlert,setIsAlert] = useState(false);
    const [center, setCenter] = useState({});
    const [renderMap,setRenderMap] = useState(false);
    const auth = localStorage.getItem('token');
    let history = useHistory();
    const onClick = () =>{
        console.log(center);
        setRenderMap(true);
    }
    useEffect(()=>{
        if(!auth){
            history.push('/login');
        }
        axios.get('/api/auth',{headers:{token: auth}})
            .then((data)=>{
                const response = data;
                console.log(response);
            }).catch((err)=>{
            console.error(err.response.data.msg)
            history.push('/login');
            getLocation()
        });
    },[auth,history]);

    useEffect(()=>{
        return(<GoogleMapReact
            bootstrapURLKeys={{ key: "AIzaSyCOhH66mvu-ZomI7nBbb-_U4gZOU3Ow8ik" }}
            defaultCenter={center}
            defaultZoom={11}
        >
        </GoogleMapReact>)
    },[center])

    const getLocation = () => {
        if (!navigator.geolocation) {
            setAlert('Geolocation is not supported by your browser');
        } else {
            navigator.geolocation.getCurrentPosition((position) => {
                setCenter({lat:position.coords.latitude,lng:position.coords.longitude});
            }, () => {
                setIsAlert(true);
                setAlert('Unable to retrieve your location');
            });
        }
    }

    return(
        <>
            <h1>Logged in</h1>
            {isAlert &&<Alert variant='danger'>{alert}</Alert>}
            <Button onClick={onClick}>map</Button>
            {renderMap && <GoogleMapReact
                bootstrapURLKeys={{ key: "AIzaSyCOhH66mvu-ZomI7nBbb-_U4gZOU3Ow8ik" }}
                defaultCenter={center}
                defaultZoom={11}
            >
            </GoogleMapReact>}
        </>
    )
}

export default Dashboard
