import {Button, Container, Nav, Navbar, NavLink} from "react-bootstrap";
import {Link, useHistory} from "react-router-dom";
import logo from './watch-dog-logo.png';
import {useEffect, useState} from "react";


const NavBar = () =>{
    let history = useHistory();
    const token = localStorage.getItem('token');
    const [signIn,setSignedIn] = useState(false);
    const handleClick = () =>{
        localStorage.removeItem('token');
        history.push('/login');
    }
    useEffect(()=>{
        if (token){setSignedIn(true)} ;
    },[]);
    return(
        <Navbar bg="dark" variant="dark">
            <Container>
                <Link to='/' style={{textDecoration: 'none'}}>
                    <Navbar.Brand><img src={logo} className="d-inline-block align-top" width={100} height={50}/></Navbar.Brand>
                </Link>
                <Nav className="me-auto">
                    <NavLink as={Link} to='/login'>Login</NavLink>
                    <NavLink as={Link} to='/register'>Register</NavLink>
                    <NavLink as={Link} to='/'>Map</NavLink>
                </Nav>
                {signIn && <Nav><Button onClick={handleClick}>Sign Out</Button></Nav>}
            </Container>
        </Navbar>
    )
};

export default NavBar
