import {useEffect, useState} from "react";
import axios from "axios";
import {useHistory} from "react-router-dom";
import {Alert} from "react-bootstrap";
import GoogleMapReact from 'google-map-react';

const Dashboard = () =>{
    const [longitude, setLongitude] = useState(null);
    const [latitude, setLatitude] = useState(null);
    const [alert, setAlert] = useState('');
    const [isAlert,setIsAlert] = useState(false);
    const auth = localStorage.getItem('token');
    let history = useHistory();
    useEffect(()=>{
        if(!auth){
            history.push('/login');
        }
        axios.get('/api/auth',{headers:{token: auth}})
            .then((data)=>{
                const response = data;
                console.log(response);
            }).catch((err)=>{
            console.error(err.response.data.msg)
            history.push('/login');
        });
        getLocation();
    },[auth,history]);

    const getLocation = () => {
        if (!navigator.geolocation) {
            setAlert('Geolocation is not supported by your browser');
        } else {
            navigator.geolocation.getCurrentPosition((position) => {
                setLatitude(position.coords.latitude);
                setLongitude(position.coords.longitude);
                const defaultProps = {
                    center:{
                        lat: longitude,

                    }
                }
            }, () => {
                setIsAlert(true);
                setAlert('Unable to retrieve your location');
            });
        }
    }

    return(
        <>
            {isAlert &&<Alert variant='danger'>{alert}</Alert>}
            <div style={{ height: '100vh', width: '100%' }}>
                <GoogleMapReact
                    bootstrapURLKeys={{ key: "AIzaSyCOhH66mvu-ZomI7nBbb-_U4gZOU3Ow8ik" }}
                    defaultCenter={{latitude,longitude}}
                    defaultZoom={11}
                />
            </div>
        </>
    )
}

export default Dashboard
